/* FlowUI â€“ guaranteed global build */
(function (w, d) {

  const FlowUI = {};

  FlowUI.init = function () {
    console.log("FlowUI init()");
    styleBody();
    styleButtons();
    animate();
  };

  function styleBody() {
    d.body.style.margin = "0";
    d.body.style.fontFamily =
      "system-ui,-apple-system,BlinkMacSystemFont,sans-serif";
    d.body.style.lineHeight = "1.6";
  }

  function styleButtons() {
    d.querySelectorAll("button").forEach(btn => {
      btn.style.padding = "14px 28px";
      btn.style.borderRadius = "999px";
      btn.style.border = "none";
      btn.style.background =
        "linear-gradient(135deg,#7f7cff,#b983ff)";
      btn.style.color = "#fff";
      btn.style.fontSize = "1rem";
      btn.style.cursor = "pointer";
      btn.style.boxShadow =
        "0 10px 30px rgba(127,124,255,.4)";
      btn.style.transition = "all .25s ease";

      btn.onmouseenter = () => btn.style.transform = "scale(1.06)";
      btn.onmouseleave = () => btn.style.transform = "scale(1)";
    });
  }

  function animate() {
    const els = d.querySelectorAll("h1,h2,p,button");
    els.forEach(el => {
      el.style.opacity = "0";
      el.style.transform = "translateY(20px)";
      el.style.transition = "all .8s ease";
    });

    const io = new IntersectionObserver(entries => {
      entries.forEach(e => {
        if (e.isIntersecting) {
          e.target.style.opacity = "1";
          e.target.style.transform = "none";
          io.unobserve(e.target);
        }
      });
    });

    els.forEach(el => io.observe(el));
  }

  /* ðŸ”´ THIS IS THE MOST IMPORTANT LINE */
  w.FlowUI = FlowUI;

})(window, document);
